import React, { createContext, useContext, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaLinkedin, FaGithub, FaEnvelope, FaPaperPlane, FaCheck, FaClock, FaPhone } from 'react-icons/fa';
import { BsCalendarEvent } from 'react-icons/bs';
import { sendEmail } from '../utils/emailService'; // Add EmailJS import

// Create the context
const ContactModalContext = createContext();

// Hook to use the contact modal
export const useContactModal = () => {
  const context = useContext(ContactModalContext);
  if (!context) {
    throw new Error('useContactModal must be used within a ContactModalProvider');
  }
  return context;
};

// Contact Modal Provider Component
export const ContactModalProvider = ({ children }) => {
  const [showContactModal, setShowContactModal] = useState(false);
  const [activeTab, setActiveTab] = useState('quick');
  const [showEmailComposer, setShowEmailComposer] = useState(false);
  const [showCalendarMessage, setShowCalendarMessage] = useState(false);
  const [emailData, setEmailData] = useState({
    to: 'ashujzawar5@gmail.com',
    subject: '',
    message: ''
  });
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [submitError, setSubmitError] = useState(false); // Add error state
  const [errorMessage, setErrorMessage] = useState(''); // Add error message state
  const [messageLength, setMessageLength] = useState(0);

  // Open contact modal
  const openContactModal = () => {
    setShowContactModal(true);
  };

  // Close contact modal and reset all states
  const closeContactModal = () => {
    setShowContactModal(false);
    setShowEmailComposer(false);
    setShowCalendarMessage(false);
    setActiveTab('quick');
    setSubmitSuccess(false);
    setSubmitError(false);
    setErrorMessage('');
  };

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));

    if (name === 'message') {
      setMessageLength(value.length);
    }
  };

  // Handle email data changes
  const handleEmailChange = (e) => {
    const { name, value } = e.target;
    setEmailData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  // UPDATED: Handle email submission with EmailJS
  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitError(false);
    setErrorMessage('');

    try {
      // Validate email composer data
      if (!emailData.subject.trim() || !emailData.message.trim()) {
        throw new Error('Please fill in both subject and message fields.');
      }

      // EmailJS template parameters for email composer
      const emailParams = {
        from_name: 'Portfolio Visitor',
        from_email: 'visitor@portfolio.com', // Placeholder email
        message: `Subject: ${emailData.subject}\n\n${emailData.message}`
      };

      console.log('ðŸ“§ Sending email composer message via EmailJS...', emailParams);
      const result = await sendEmail(emailParams);

      if (result.success) {
        console.log('âœ… Email composer sent successfully!', result);
        setIsSubmitting(false);
        setSubmitSuccess(true);

        // Reset email composer
        setEmailData({
          to: 'ashujzawar5@gmail.com',
          subject: '',
          message: ''
        });

        // Close email composer after success
        setTimeout(() => {
          setShowEmailComposer(false);
          setSubmitSuccess(false);
          setShowContactModal(false);
        }, 2500);
      } else {
        throw new Error(result.error || 'Failed to send email');
      }

    } catch (error) {
      console.error('âŒ Email composer failed:', error);
      setIsSubmitting(false);
      setSubmitError(true);
      setErrorMessage(error.message || 'Failed to send email. Please try again.');

      setTimeout(() => {
        setSubmitError(false);
        setErrorMessage('');
      }, 5000);
    }
  };

  // UPDATED: Handle form submission with EmailJS
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitError(false);
    setErrorMessage('');

    try {
      // Validate form data
      if (!formData.name.trim() || !formData.email.trim() || !formData.message.trim()) {
        throw new Error('Please fill in all required fields.');
      }

      // Email validation
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(formData.email)) {
        throw new Error('Please enter a valid email address.');
      }

      // Message length validation
      if (formData.message.length < 10) {
        throw new Error('Message must be at least 10 characters long.');
      }

      // EmailJS template parameters - these MUST match your template variables
      const emailParams = {
        from_name: formData.name,
        from_email: formData.email,
        message: formData.message
      };

      console.log('ðŸ“§ Sending contact form via EmailJS...', emailParams);
      const result = await sendEmail(emailParams);

      if (result.success) {
        console.log('âœ… Contact form sent successfully!', result);
        setIsSubmitting(false);
        setSubmitSuccess(true);

        // Reset form
        setFormData({
          name: '',
          email: '',
          message: ''
        });
        setMessageLength(0);

        // Close modal after success message
        setTimeout(() => {
          setShowContactModal(false);
          setSubmitSuccess(false);
        }, 2500);
      } else {
        throw new Error(result.error || 'Failed to send message');
      }

    } catch (error) {
      console.error('âŒ Contact form failed:', error);
      setIsSubmitting(false);
      setSubmitError(true);
      setErrorMessage(error.message || 'Failed to send message. Please try again.');

      // Hide error message after 5 seconds
      setTimeout(() => {
        setSubmitError(false);
        setErrorMessage('');
      }, 5000);
    }
  };

  // Handle calendar booking click
  const handleCalendarClick = () => {
    setShowCalendarMessage(true);
    setTimeout(() => {
      setShowCalendarMessage(false);
    }, 4000);
  };

  // Handle phone call
  const handlePhoneCall = () => {
    window.open('tel:+17043639906');
  };

  // Open email composer
  const openEmailComposer = () => {
    setShowEmailComposer(true);
    setSubmitError(false);
    setErrorMessage('');
  };

